mapper.update:
update dish
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser},
where id = #{id}

mapper.select:
select dish.*, category.name as categoryName from dish left outer join category on dish.category_id = category.id
and dish.name like concat('%', #{name}, '%')
and d.category_id = #{categoryId}
and d.status = #{status}
order by dish.create_time desc
